#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main()
{
    char    *largeNumber;
    long int     largestProduct;
    long int     n;
    int     i;
    int     j;

    largeNumber = "";
    largestProduct = 0;
    n = 1;
    i = 0;
    while (i < 950-13)
    {
        j = 0;
        while (j < 13)
        {
            n = n * (largeNumber[i + j] - '0');
            j++;
        }
        if (n > largestProduct)
            largestProduct = n;
        n = 1;
        i++;
    }
    printf("largest produit in a series of 13 is : %ld\n", largestProduct);
    return (0);
}